using System;using Microsoft.Xrm.Sdk;using Microsoft.Xrm.Sdk.Messages;namespace XrmSample.Plugins.Plugins.Assignments{public class AccountAutoAssignPostCreatePlugin:XrmSample.Plugins.PluginBase{protected override void ExecutePlugin(IPluginExecutionContext c,IOrganizationService s,ITracingService t){if(c.MessageName!="Create")return;if(!(c.OutputParameters.Contains("id")&&c.OutputParameters["id"] is Guid id))return;Guid teamId=Guid.Empty;if(teamId!=Guid.Empty){var req=new AssignRequest{Assignee=new EntityReference("team",teamId),Target=new EntityReference("account",id)};s.Execute(req);} }}}