using System;using Microsoft.Xrm.Sdk;namespace XrmSample.Plugins.Plugins{public class FollowUpTaskPostCreatePlugin:XrmSample.Plugins.PluginBase{public FollowUpTaskPostCreatePlugin(string u,string s):base(u,s){}protected override void ExecutePlugin(IPluginExecutionContext c,IOrganizationService svc,ITracingService t){if(c.MessageName!="Create")return;if(!(c.OutputParameters.Contains("id")&&c.OutputParameters["id"] is Guid id))return;var task=new Entity("task");task["subject"]="Follow up: New account created";task["description"]="Please reach out to the new account within 7 days.";task["scheduledend"]=DateTime.UtcNow.AddDays(7);task["regardingobjectid"]=new EntityReference("account",id);svc.Create(task);}}}